INSERT INTO `xmw_announcement` VALUES ('0e479268-99e9-4090-9efa-b3b246c0e6df', 'dac1e100-a6ad-48c4-9963-b3be37c6443c', '李晓鹏被双开', '<p><a href=\"https://s.weibo.com/weibo?q=%23%E5%85%89%E5%A4%A7%E9%9B%86%E5%9B%A2%E5%8E%9F%E8%91%A3%E4%BA%8B%E9%95%BF%E6%9D%8E%E6%99%93%E9%B9%8F%E8%A2%AB%E5%8F%8C%E5%BC%80%23\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\">#光大集团原董事长李晓鹏被双开#</a><span style=\"color: rgb(51, 51, 51);\">【</span><a href=\"https://s.weibo.com/weibo?q=%23%E6%9D%8E%E6%99%93%E9%B9%8F%E8%A2%AB%E5%8F%8C%E5%BC%80%23\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\">#李晓鹏被双开#</a><span style=\"color: rgb(51, 51, 51);\">】经中共中央批准，中央纪委国家监委对中国光大集团股份公司原党委书记、董事长李晓鹏严重违纪违法问题进行了立案审查调查。 经查，李晓鹏丧失理想信念，背弃初心使命，对党不忠诚不老实，对抗组织审查；无视中央八项规定精神，违规收受礼品礼金、接受宴请；违反组织原则，不按规定报告个人有关事项，在干部选拔任用等方面为他人谋利；违规借用管理和服务对象钱款、持有非上市公司股份，利用职权为亲属经营活动谋利；泄露尚未公开的干部选拔任用事项；治家不严，家风不正；既想当官又想发财，长期以权谋私，“靠金融吃金融”，利用职务便利为他人在贷款融资、业务承揽等方面谋利，并非法收受巨额财物。 李晓鹏严重违反党的政治纪律、组织纪律、廉洁纪律、工作纪律和生活纪律，构成严重职务违法并涉嫌受贿犯罪，且在党的十八大后不收敛、不收手，性质严重，影响恶劣，应予严肃处理。依据《中国共产党纪律处分条例》《中华人民共和国监察法》《中华人民共和国公职人员政务处分法》等有关规定，经中央纪委常委会会议研究并报中共中央批准，决定给予李晓鹏开除党籍处分；由国家监委给予其开除公职处分；收缴其违纪违法所得；将其涉嫌犯罪问题移送检察机关依法审查起诉，所涉财物一并移送。（中央纪委国家监委网站）</span></p>', '3', 1, 1, '2023-10-09 13:45:04', '2023-10-16 15:03:35');
INSERT INTO `xmw_announcement` VALUES ('2244a7e7-f92b-4645-9c72-384a227053f4', 'dac1e100-a6ad-48c4-9963-b3be37c6443c', '亚运会中国代表团201金收官', '<p><span style=\"color: rgb(51, 51, 51);\">【骄傲转发！</span><a href=\"https://s.weibo.com/weibo?q=%23%E4%BA%9A%E8%BF%90%E4%BC%9A%E4%B8%AD%E5%9B%BD%E4%BB%A3%E8%A1%A8%E5%9B%A2201%E9%87%91%E6%94%B6%E5%AE%98%23\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\">#亚运会中国代表团201金收官#</a><span style=\"color: rgb(51, 51, 51);\"><img src=\"https://face.t.sinajs.cn/t4/appstyle/expression/ext/normal/14/2018new_weiwu_org.png\" alt=\"[威武]\">】杭州亚运会，中国代表团共获得201金111银71铜，共383枚奖牌，创造历史。致敬拼搏精神，致敬永不停歇的奋进脚步！今晚8点，</span><a href=\"https://s.weibo.com/weibo/%23%E4%BA%9A%E8%BF%90%E9%97%AD%E5%B9%95%E5%BC%8F%23\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\">#亚运闭幕式#</a><span style=\"color: rgb(51, 51, 51);\">举行，一起期待！</span></p>', '4', 1, 1, '2023-10-09 13:43:18', '2023-10-09 13:43:37');
INSERT INTO `xmw_announcement` VALUES ('2e86e528-1401-4c12-ae9e-30508c21c57f', 'bf75a509-f90e-4a29-8bf7-470b581550f6', '花西子就被传谣为日本品牌向警方报案', '<p><span style=\"color: rgb(51, 51, 51);\">近日，随着某知名主播不当言论链式风波甚嚣尘上，“花西子是日本品牌”等截图在网络上流传。对此，花西子表示：花西子品牌名中的“花”，取自于品牌理念中的“以花养妆”；“西子”则指的是西湖，灵感来自苏东坡的名句“欲把西湖比西子，淡妆浓抹总相宜”。</span></p><p><span style=\"color: rgb(51, 51, 51);\">同时，花西子接受媒体采访中对探访视频表示：2019年曾与日本某研究所有过短期产研和生产合作，2020年已停止合作。随着国内美妆研发及产业链的综合实力的持续上升，目前花西子的研发中心及生产工厂100%都在国内。</span></p><p><span style=\"color: rgb(51, 51, 51);\">据花西子产品外包装上显示，委托方为中国杭州，产地为中国上海，未有任何日本相关工厂信息。</span></p><p><span style=\"color: rgb(51, 51, 51);\">据了解，此前花西子还曾获得由新华社颁发的“中国品牌全球传播力”top10。综上网传“花西子产地为日本”“花西子是日本品牌”皆为谣言，并就此向警方报案。</span></p>', '1', 1, 1, '2023-09-12 16:22:13', '2023-10-09 10:00:43');
INSERT INTO `xmw_announcement` VALUES ('31fdb366-bff0-4d0c-8d2e-3c530225b2d9', 'af968b95-5f2e-4f87-8002-e6ac23b72a23', '女子买高铁二等座嫌冷大闹要毛毯', '<p><span style=\"color: rgb(51, 51, 51);\">9月11日，G2217次列车上一女子嫌二等座车厢内空调温度低，大吵着要求关空调，并要求列车员提供毛毯。视频中，女子大吵大闹，称自己是癌症病人，身体不好。同车乘客称，女子向乘务员要毯子，工作人员称商务座才有毯子。女子不依不饶大喊要投诉，最后乘务员无奈给她送来毯子。</span></p>', '3', 1, 1, '2023-09-12 15:19:02', '2023-10-08 11:44:34');
INSERT INTO `xmw_announcement` VALUES ('43c45cab-7449-4a52-9204-b828f067e0f9', '7887f679-32f0-47e2-8137-9d966281aa48', '海滩走失女童父亲深夜发布多张照片', '<p><span style=\"color: rgb(51, 51, 51);\">上海4岁失联女童引发社会高度关注，不仅家人正在急切寻找女童，她也牵动着无数网友的心，女童父亲深夜发布多张照片，网友提供疑似失踪前画面。10月6日深夜23:05，黄元芯爸爸公布了多张带时间点标注的照片，称都是走失女童黄元芯的照片，来源于网友提供的线索。其称第一张照片摄于17:33，女童正跟爸爸在一起；目前最后拍摄到的照片，摄于17:53。</span></p>', '3', 1, 0, '2023-10-07 10:29:25', '2023-10-07 10:29:25');
INSERT INTO `xmw_announcement` VALUES ('556f48c6-c57b-4a36-aa02-a622b13b9d71', 'af968b95-5f2e-4f87-8002-e6ac23b72a23', '来杭州感受体育之美', '<p><span style=\"color: rgb(51, 51, 51);\">杭州第19届亚运会火炬传递于8日在杭州启动，目前正在浙江省内11个地级市依次开展传递活动。火炬传递有何亮点新意？传递路线如何设计？记者采访了杭州亚运会火炬传递指挥中心副指挥长杜作锋和部分火炬手，听他们讲述火炬传递背后的故事。</span></p>', '2', 1, 1, '2023-09-12 15:17:38', '2023-09-12 15:17:38');
INSERT INTO `xmw_announcement` VALUES ('6e249d29-b755-49a5-afc9-02393e35daa4', '7887f679-32f0-47e2-8137-9d966281aa48', '专家建议增加假期不要调休', '<p><span style=\"color: rgb(51, 51, 51);\">【国庆8天假期结束！</span><a href=\"https://s.weibo.com/weibo?q=%23%E4%BB%8A%E5%B9%B4%E7%9A%84%E6%B3%95%E5%AE%9A%E8%8A%82%E5%81%87%E6%97%A5%E8%BF%87%E5%AE%8C%E4%BA%86%23\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\">#今年的法定节假日过完了#</a><span style=\"color: rgb(51, 51, 51);\">，大家需连上7天班】</span><a href=\"https://s.weibo.com/weibo?q=%23%E4%B8%93%E5%AE%B6%E5%BB%BA%E8%AE%AE%E5%A2%9E%E5%8A%A0%E5%81%87%E6%9C%9F%E4%B8%8D%E8%A6%81%E8%B0%83%E4%BC%91%23\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\">#专家建议增加假期不要调休#</a><span style=\"color: rgb(51, 51, 51);\">10月7日消息，国庆假期已经结束了，大家玩的怎么样？2023年的十一黄金周已在10月6日画上句号，这意味着今年的所有法定假日已经全部度过。八天假期很短，即将到来的连续七天工作日会不会很长？</span></p><p><span style=\"color: rgb(51, 51, 51);\">按照规定，2023年中秋节、国庆节放假安排：2023年9月29日（星期五）至10月6日（星期五）放假调休，共8天 。10月7日（星期六）、10月8日（星期日）上班，所以你准备连上7天班了吗？</span></p><p><span style=\"color: rgb(51, 51, 51);\">8天假期之后周末两天都要上班，等于是连上7天班，甚至周日单休的朋友要连上8天。</span></p><p><span style=\"color: rgb(51, 51, 51);\">对此，清华大学五道口金融学院副院长、金融学讲席教授田轩谈到调休表示，确实调休可以强化释放部分的购买力，但是调休也还是要合理，不要因为调休给居民工作、生活带来困扰。</span></p><p><span style=\"color: rgb(51, 51, 51);\">所以我想建议调休的原则还是适当增加和延长假期，逢周末还是顺延，不要调休，释放快乐消费的活力。”田轩说道。</span></p><p><span style=\"color: rgb(51, 51, 51);\">﻿闹闹哄哄的八天长假结束了，上班族将迎来连续七个工作日。经文化和旅游部数据中心测算，中秋节、国庆节假期8天，国内旅游出游人数8.26亿人次，按可比口径同比增长71.3%，按可比口径较2019年增长4.1%；实现国内旅游收入7534.3亿元，按可比口径同比增长129.5%，按可比口径较2019年增长1.5%。</span></p><p><span style=\"color: rgb(51, 51, 51);\">这是世界上规模最大的短时人口迁徙，8天时间平均人均次消费912元。这个消费，如果扣除假期涨价的机票酒店等交通住宿费用，其他消费还能有多少？看到一个短视频说不少景区餐饮和卖纪念品的商家，假期都是亏损的，游客们光看不买，整个就是一个穷游。</span></p><p><span style=\"color: rgb(51, 51, 51);\">把两个公众假期人为调休成8天长假，政府的良苦用心是希望能够刺激消费，带动一下疲弱的经济。殊不知光放假不发钱，是很难刺激消费的。当下既不是通胀也不是通缩，而是通通都没钱了。有钱有闲的生活，令人羡慕。但是有闲没钱的日子却很憋屈，8天不能总糗在家里啊，实在不行就开车在高速上兜兜风，也算是为三桶油做贡献了</span></p>', '4', 1, 1, '2023-10-07 10:03:30', '2023-10-09 10:00:23');
INSERT INTO `xmw_announcement` VALUES ('7115bd27-8bc3-43c7-8c3f-912d943d9d7d', 'dac1e100-a6ad-48c4-9963-b3be37c6443c', '月薪4000的小镇青年背过万的包', '<p><span style=\"color: rgb(51, 51, 51);\">【</span><a href=\"https://s.weibo.com/weibo?q=%23%E6%9C%88%E8%96%AA4000%E7%9A%84%E5%B0%8F%E9%95%87%E9%9D%92%E5%B9%B4%E8%83%8C%E8%BF%87%E4%B8%87%E7%9A%84%E5%8C%85%23\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\">#月薪4000的小镇青年背过万的包#</a><a href=\"https://s.weibo.com/weibo?q=%23%E5%B0%8F%E9%95%87%E9%9D%92%E5%B9%B4%E4%B8%BA%E4%BD%95%E6%95%A2%E6%B6%88%E8%B4%B9%23\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\">#小镇青年为何敢消费#</a><span style=\"color: rgb(51, 51, 51);\">】长假回老家一趟让我清醒了，千万不要小瞧县城青年的消费能力。月薪4000的朋友，背着过万的包，聚会花钱也不手软，中高端旅游一年一次......这种想吃就吃、说玩就走的生活态度让我清楚认识到，新一代小镇青年，正在成为隐形的“消费巨人”。摩根士丹利中国首席经济学家刑自强表示，小镇青年将成为今后消费市场的主力军，预计到2030年，三四线城市居民消费将达到45万亿元人民币。</span></p>', '3', 1, 0, '2023-10-09 13:44:06', '2023-10-09 13:44:06');
INSERT INTO `xmw_announcement` VALUES ('79adfb8c-4c5a-4f50-af35-85dd60b0b842', '7887f679-32f0-47e2-8137-9d966281aa48', '亚运会闭幕式只用一块草坪', '<p><span style=\"color: rgb(51, 51, 51);\">【</span><a href=\"https://s.weibo.com/weibo?q=%23%E4%BA%9A%E8%BF%90%E4%BC%9A%E9%97%AD%E5%B9%95%E5%BC%8F%E5%8F%AA%E7%94%A8%E4%B8%80%E5%9D%97%E8%8D%89%E5%9D%AA%23\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\">#亚运会闭幕式只用一块草坪#</a><span style=\"color: rgb(51, 51, 51);\">】10月8日，杭州亚运会闭幕式将在大莲花举行。杭州亚运会闭幕式总导演沙晓岚介绍，运动员们将再度回到熟悉的“大莲花”，而场馆中间的草坪也将成为杭州亚运会首创的一块数控草坪。看似很平常的一块草坪，其实这里面藏着近4万个发光点，可以和节目可以产生很多图案、颜色。据了解，除了数控草坪以外，开幕式上令人震撼的数字火炬手在闭幕式上，又如约来了。在闭幕式上，全球的网友都可以领取一份象征亚运精神的“星光数字纪念章”。</span></p>', '2', 1, 0, '2023-10-07 10:28:46', '2023-10-07 10:28:46');
INSERT INTO `xmw_announcement` VALUES ('91f0d138-651a-44d1-bf8d-9200bb8429cd', 'a7e1d550-c351-4d19-8873-268912f73584', '民警给走失小女孩上了堂甜甜的安全课', '<p><span style=\"color: rgb(51, 51, 51);\">9月30日17时，北京公交警方接到江苏来京旅游的严女士报警，称在地铁19号线与8岁的女儿婷婷走散。民警赵玲涛在景风门站寻找的过程中，恰好巧遇正在向车站工作人员求助的婷婷。见婷婷惊魂甫定，赵玲涛用温柔的口吻不断安抚她，现场传授了很多安全防范常识。随后严女士赶来，母女团圆。</span></p>', '1', 1, 1, '2023-10-07 10:33:39', '2023-10-07 10:33:39');
INSERT INTO `xmw_announcement` VALUES ('9620ef61-b8c6-477e-b8c9-a65455849d4c', 'af968b95-5f2e-4f87-8002-e6ac23b72a23', '华为问界新M7', '<p><strong style=\"color: rgb(51, 51, 51);\">问界新M7将于9月12日在上海举办发布会。据悉，此次发布会的看点之一就是问界新M7搭载的鸿蒙智能座舱3.0。作为25万级别车型，车内超大空间不仅可实现主副驾驶双排休憩，而且可切换成2米长度的大床模式。此次问界新M7将带来全新大空间体验</strong><span style=\"color: rgb(51, 51, 51);\">。</span><strong style=\"color: rgb(51, 51, 51);\">﻿</strong></p>', '4', 1, 1, '2023-09-12 15:18:25', '2023-09-12 16:12:48');
INSERT INTO `xmw_announcement` VALUES ('b0e0637c-db39-4882-b902-977c6d33a6c3', 'a7e1d550-c351-4d19-8873-268912f73584', '官方通报已婚女子陪领导喝酒醉卧街头', '<p><span style=\"color: rgb(51, 51, 51);\">【官方通报</span><a href=\"https://s.weibo.com/weibo?q=%23%E5%A5%B3%E5%AD%90%E9%99%AA%E9%A2%86%E5%AF%BC%E5%96%9D%E9%85%92%E9%86%89%E5%8D%A7%E8%A1%97%E5%A4%B4%23\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\">#女子陪领导喝酒醉卧街头#</a><span style=\"color: rgb(51, 51, 51);\">：醉酒女子未婚，未发现不正当关系】据</span><a href=\"https://weibo.com/n/%E6%96%B0%E4%BA%AC%E6%8A%A5\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\">@新京报</a><span style=\"color: rgb(51, 51, 51);\">&nbsp;报道：8月31日，网友发视频举报一女子“婚内出轨”，“多次陪领导喝酒醉卧街头”。10月6日，山东泰安通报“女子陪领导喝酒醉卧街头”调查情况。视频中醉酒女子系泰安高新区未婚副科级干部许某，视频发布者系已婚的孟某某，8月30日许某和孟某某因感情纠纷争执，将以往录制的许某醉酒视频剪辑后发至网络。调查发现，许某及视频中的两名张姓人员存在违规接受宴请问题，其中张某（男）系泰安高新区党工委委员、管委会副主任，张某（女）系泰安高新建设集团人资部部长，目前三人均已被处理。</span></p>', '1', 1, 0, '2023-10-07 10:32:26', '2023-10-07 17:00:48');
INSERT INTO `xmw_announcement` VALUES ('c01675fd-443a-4e5e-8c33-36fa5a3bbb9d', 'bf75a509-f90e-4a29-8bf7-470b581550f6', '李佳琦们别再飘了', '<p><em style=\"color: rgb(51, 51, 51);\"><u>&nbsp;曾经喊着“把价格打下来”的李佳琦，因在最近一场直播中回怼网友频登热搜。一时的口无遮拦，招致网络舆论的愤怒声讨。大家发现，不少网红、主播赚钱之前都很谦卑，赚钱之后变得很膨胀。但任何行业都离不开柴米油盐精打细算的普通人，一旦“飘”了，忘记了做好服务的初心，注定被厌弃淘汰。</u></em></p>', '1', 1, 0, '2023-09-12 16:16:24', '2023-09-21 14:59:25');
INSERT INTO `xmw_announcement` VALUES ('c0eb77d4-43b1-4e20-8adf-7c5f399cf54a', 'bf75a509-f90e-4a29-8bf7-470b581550f6', '李佳琦称身上羽毛是所有女生赋予的', '<p><span style=\"color: rgb(51, 51, 51);\">“李佳琦带货怼网友”的话题在网络引起广泛讨论，有数据显示，在“2021年度中国网络主播年度净收入百强榜”中，李佳琦以年度净收入18.553亿元高居榜首，年收入超过大部分上市公司的业绩。你如何看待少数网红直播带货高收入现象？</span></p>', '2', 1, 1, '2023-09-12 16:21:30', '2023-10-07 17:54:54');
INSERT INTO `xmw_announcement` VALUES ('d13745ca-368f-4c04-9047-4641032a2294', 'bf75a509-f90e-4a29-8bf7-470b581550f6', '习近平会见乌兹别克斯坦总', '<p><span style=\"color: rgb(51, 51, 51);\">【独家视频丨</span><a href=\"https://s.weibo.com/weibo?q=%23%E4%B9%A0%E8%BF%91%E5%B9%B3%E4%BC%9A%E8%A7%81%E4%B9%8C%E5%85%B9%E5%88%AB%E5%85%8B%E6%96%AF%E5%9D%A6%E6%80%BB%E7%BB%9F%23\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\">#习近平会见乌兹别克斯坦总统#</a><span style=\"color: rgb(51, 51, 51);\">】10月17日下午，国家主席习近平在人民大会堂会见来华出席第三届“一带一路”国际合作高峰论坛的乌兹别克斯坦总统米尔济约耶夫。</span></p><p><br></p><p><span style=\"color: rgb(51, 51, 51);\">习近平指出，很高兴同老朋友再次会面。欢迎你来华出席第三届“一带一路”国际合作高峰论坛。今年5月，我们在西安成功会晤，就两国关系发展作出战略规划。我高兴地看到，这些共识正在得到全面落实。前不久，乌兹别克斯坦举行总统选举，你高票当选连任，充分体现了贵国人民对你的衷心拥护和支持。我再次向你表示祝贺。</span></p><p><br></p><p><span style=\"color: rgb(51, 51, 51);\">当前，中国人民正在以中国式现代化全面推进强国建设、民族复兴伟业。乌方出台“乌兹别克斯坦-2030”战略。两国发展目标相似，理念契合。中方愿以共建“一带一路”倡议提出10周年为契机，同乌方在国家现代化进程中互学互鉴、相互支持、彼此成就，携手建设中乌命运共同体，为地区和平和繁荣注入更多确定性和正能量</span></p>', '3', 1, 1, '2023-10-18 09:39:31', '2023-10-18 09:39:31');
