INSERT INTO `xmw_logs` VALUES ('2a7c3933-c304-40bb-ae4e-05baec6647a3', 'a7e1d550-c351-4d19-8873-268912f73584', '创建活动公告：官方通报已婚女子陪领导喝酒醉卧街头', '::ffff:127.0.0.1', 'http://localhost:8888/administrative/announcement', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/117.0.0.0 Safari/537.36', '{\"type\": \"1\", \"title\": \"官方通报已婚女子陪领导喝酒醉卧街头\", \"pinned\": 0, \"status\": 1, \"content\": \"<p><span style=\\\"color: rgb(51, 51, 51);\\\">【官方通报</span><a href=\\\"https://s.weibo.com/weibo?q=%23%E5%A5%B3%E5%AD%90%E9%99%AA%E9%A2%86%E5%AF%BC%E5%96%9D%E9%85%92%E9%86%89%E5%8D%A7%E8%A1%97%E5%A4%B4%23\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\\\">#女子陪领导喝酒醉卧街头#</a><span style=\\\"color: rgb(51, 51, 51);\\\">：醉酒女子未婚，未发现不正当关系】据</span><a href=\\\"https://weibo.com/n/%E6%96%B0%E4%BA%AC%E6%8A%A5\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\\\">@新京报</a><span style=\\\"color: rgb(51, 51, 51);\\\">&nbsp;报道：8月31日，网友发视频举报一女子“婚内出轨”，“多次陪领导喝酒醉卧街头”。10月6日，山东泰安通报“女子陪领导喝酒醉卧街头”调查情况。视频中醉酒女子系泰安高新区未婚副科级干部许某，视频发布者系已婚的孟某某，8月30日许某和孟某某因感情纠纷争执，将以往录制的许某醉酒视频剪辑后发至网络。调查发现，许某及视频中的两名张姓人员存在违规接受宴请问题，其中张某（男）系泰安高新区党工委委员、管委会副主任，张某（女）系泰安高新建设集团人资部部长，目前三人均已被处理。</span></p>\"}', 'POST', '/v1/administrative/announcement', '2023-10-07 10:32:26', '2023-10-07 10:32:26');
