INSERT INTO `xmw_logs` VALUES ('8d3b3b92-628e-4d5c-87a8-cbf9002d251f', 'a7e1d550-c351-4d19-8873-268912f73584', '创建活动公告：民警给走失小女孩上了堂甜甜的安全课', '::ffff:127.0.0.1', 'http://localhost:8888/administrative/announcement', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/117.0.0.0 Safari/537.36', '{\"type\": \"1\", \"title\": \"民警给走失小女孩上了堂甜甜的安全课\", \"pinned\": 1, \"status\": 1, \"content\": \"<p><span style=\\\"color: rgb(51, 51, 51);\\\">9月30日17时，北京公交警方接到江苏来京旅游的严女士报警，称在地铁19号线与8岁的女儿婷婷走散。民警赵玲涛在景风门站寻找的过程中，恰好巧遇正在向车站工作人员求助的婷婷。见婷婷惊魂甫定，赵玲涛用温柔的口吻不断安抚她，现场传授了很多安全防范常识。随后严女士赶来，母女团圆。</span></p>\"}', 'POST', '/v1/administrative/announcement', '2023-10-07 10:33:39', '2023-10-07 10:33:39');
