INSERT INTO `xmw_logs` VALUES ('8ed23667-bfcc-4743-b81f-394c7ea275d9', '7887f679-32f0-47e2-8137-9d966281aa48', '创建活动公告：亚运会闭幕式只用一块草坪', '::ffff:127.0.0.1', 'http://localhost:8888/administrative/announcement', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/117.0.0.0 Safari/537.36', '{\"type\": \"2\", \"title\": \"亚运会闭幕式只用一块草坪\", \"pinned\": 0, \"status\": 1, \"content\": \"<p><span style=\\\"color: rgb(51, 51, 51);\\\">【</span><a href=\\\"https://s.weibo.com/weibo?q=%23%E4%BA%9A%E8%BF%90%E4%BC%9A%E9%97%AD%E5%B9%95%E5%BC%8F%E5%8F%AA%E7%94%A8%E4%B8%80%E5%9D%97%E8%8D%89%E5%9D%AA%23\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\\\">#亚运会闭幕式只用一块草坪#</a><span style=\\\"color: rgb(51, 51, 51);\\\">】10月8日，杭州亚运会闭幕式将在大莲花举行。杭州亚运会闭幕式总导演沙晓岚介绍，运动员们将再度回到熟悉的“大莲花”，而场馆中间的草坪也将成为杭州亚运会首创的一块数控草坪。看似很平常的一块草坪，其实这里面藏着近4万个发光点，可以和节目可以产生很多图案、颜色。据了解，除了数控草坪以外，开幕式上令人震撼的数字火炬手在闭幕式上，又如约来了。在闭幕式上，全球的网友都可以领取一份象征亚运精神的“星光数字纪念章”。</span></p>\"}', 'POST', '/v1/administrative/announcement', '2023-10-07 10:28:46', '2023-10-07 10:28:46');
INSERT INTO `xmw_logs` VALUES ('8ee72d02-d13f-4be0-857d-3491ca07b466', 'bf75a509-f90e-4a29-8bf7-470b581550f6', '编辑活动公告：李晓鹏被双开', '::ffff:127.0.0.1', 'http://localhost:8888/administrative/announcement', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/118.0.0.0 Safari/537.36', '{\"type\": \"3\", \"title\": \"李晓鹏被双开\", \"pinned\": 1, \"status\": 1, \"content\": \"<p><a href=\\\"https://s.weibo.com/weibo?q=%23%E5%85%89%E5%A4%A7%E9%9B%86%E5%9B%A2%E5%8E%9F%E8%91%A3%E4%BA%8B%E9%95%BF%E6%9D%8E%E6%99%93%E9%B9%8F%E8%A2%AB%E5%8F%8C%E5%BC%80%23\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"background-color: rgb(255, 255, 255); color: rgb(235, 115, 64);\\\">#光大集团原董事长李晓鹏被双开#</a><span style=\\\"color: rgb(51, 51, 51);\\\">【</span><a href=\\\"https://s.weibo.com/weibo?q=%23%E6%9D%8E%E6%99%93%E9%B9%8F%E8%A2%AB%E5%8F%8C%E5%BC%80%23\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\\\">#李晓鹏被双开#</a><span style=\\\"color: rgb(51, 51, 51);\\\">】经中共中央批准，中央纪委国家监委对中国光大集团股份公司原党委书记、董事长李晓鹏严重违纪违法问题进行了立案审查调查。 经查，李晓鹏丧失理想信念，背弃初心使命，对党不忠诚不老实，对抗组织审查；无视中央八项规定精神，违规收受礼品礼金、接受宴请；违反组织原则，不按规定报告个人有关事项，在干部选拔任用等方面为他人谋利；违规借用管理和服务对象钱款、持有非上市公司股份，利用职权为亲属经营活动谋利；泄露尚未公开的干部选拔任用事项；治家不严，家风不正；既想当官又想发财，长期以权谋私，“靠金融吃金融”，利用职务便利为他人在贷款融资、业务承揽等方面谋利，并非法收受巨额财物。 李晓鹏严重违反党的政治纪律、组织纪律、廉洁纪律、工作纪律和生活纪律，构成严重职务违法并涉嫌受贿犯罪，且在党的十八大后不收敛、不收手，性质严重，影响恶劣，应予严肃处理。依据《中国共产党纪律处分条例》《中华人民共和国监察法》《中华人民共和国公职人员政务处分法》等有关规定，经中央纪委常委会会议研究并报中共中央批准，决定给予李晓鹏开除党籍处分；由国家监委给予其开除公职处分；收缴其违纪违法所得；将其涉嫌犯罪问题移送检察机关依法审查起诉，所涉财物一并移送。（中央纪委国家监委网站）</span></p>\", \"announcement_id\": \"0e479268-99e9-4090-9efa-b3b246c0e6df\"}', 'POST', '/v1/administrative/announcement', '2023-10-16 10:42:39', '2023-10-16 10:42:39');
