INSERT INTO `xmw_logs` VALUES ('98b2bef8-5b53-4fb6-a6e7-d209c35e01be', 'bf75a509-f90e-4a29-8bf7-470b581550f6', '创建活动公告：习近平会见乌兹别克斯坦总', '::ffff:127.0.0.1', 'http://localhost:8888/administrative/announcement', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/118.0.0.0 Safari/537.36', '{\"type\": \"3\", \"title\": \"习近平会见乌兹别克斯坦总\", \"pinned\": 1, \"status\": 1, \"content\": \"<p><span style=\\\"color: rgb(51, 51, 51);\\\">【独家视频丨</span><a href=\\\"https://s.weibo.com/weibo?q=%23%E4%B9%A0%E8%BF%91%E5%B9%B3%E4%BC%9A%E8%A7%81%E4%B9%8C%E5%85%B9%E5%88%AB%E5%85%8B%E6%96%AF%E5%9D%A6%E6%80%BB%E7%BB%9F%23\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\\\">#习近平会见乌兹别克斯坦总统#</a><span style=\\\"color: rgb(51, 51, 51);\\\">】10月17日下午，国家主席习近平在人民大会堂会见来华出席第三届“一带一路”国际合作高峰论坛的乌兹别克斯坦总统米尔济约耶夫。</span></p><p><br></p><p><span style=\\\"color: rgb(51, 51, 51);\\\">习近平指出，很高兴同老朋友再次会面。欢迎你来华出席第三届“一带一路”国际合作高峰论坛。今年5月，我们在西安成功会晤，就两国关系发展作出战略规划。我高兴地看到，这些共识正在得到全面落实。前不久，乌兹别克斯坦举行总统选举，你高票当选连任，充分体现了贵国人民对你的衷心拥护和支持。我再次向你表示祝贺。</span></p><p><br></p><p><span style=\\\"color: rgb(51, 51, 51);\\\">当前，中国人民正在以中国式现代化全面推进强国建设、民族复兴伟业。乌方出台“乌兹别克斯坦-2030”战略。两国发展目标相似，理念契合。中方愿以共建“一带一路”倡议提出10周年为契机，同乌方在国家现代化进程中互学互鉴、相互支持、彼此成就，携手建设中乌命运共同体，为地区和平和繁荣注入更多确定性和正能量</span></p>\"}', 'POST', '/v1/administrative/announcement', '2023-10-18 09:39:31', '2023-10-18 09:39:31');
