INSERT INTO `xmw_logs` VALUES ('fedf8889-2763-467b-b2ef-4e15f18d8bb3', 'dac1e100-a6ad-48c4-9963-b3be37c6443c', '创建活动公告：月薪4000的小镇青年背过万的包', '::ffff:127.0.0.1', 'http://localhost:8888/administrative/announcement', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/117.0.0.0 Safari/537.36', '{\"type\": \"3\", \"title\": \"月薪4000的小镇青年背过万的包\", \"pinned\": 0, \"status\": 1, \"content\": \"<p><span style=\\\"color: rgb(51, 51, 51);\\\">【</span><a href=\\\"https://s.weibo.com/weibo?q=%23%E6%9C%88%E8%96%AA4000%E7%9A%84%E5%B0%8F%E9%95%87%E9%9D%92%E5%B9%B4%E8%83%8C%E8%BF%87%E4%B8%87%E7%9A%84%E5%8C%85%23\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\\\">#月薪4000的小镇青年背过万的包#</a><a href=\\\"https://s.weibo.com/weibo?q=%23%E5%B0%8F%E9%95%87%E9%9D%92%E5%B9%B4%E4%B8%BA%E4%BD%95%E6%95%A2%E6%B6%88%E8%B4%B9%23\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(235, 115, 64); background-color: rgb(255, 255, 255);\\\">#小镇青年为何敢消费#</a><span style=\\\"color: rgb(51, 51, 51);\\\">】长假回老家一趟让我清醒了，千万不要小瞧县城青年的消费能力。月薪4000的朋友，背着过万的包，聚会花钱也不手软，中高端旅游一年一次......这种想吃就吃、说玩就走的生活态度让我清楚认识到，新一代小镇青年，正在成为隐形的“消费巨人”。摩根士丹利中国首席经济学家刑自强表示，小镇青年将成为今后消费市场的主力军，预计到2030年，三四线城市居民消费将达到45万亿元人民币。</span></p>\"}', 'POST', '/v1/administrative/announcement', '2023-10-09 13:44:06', '2023-10-09 13:44:06');
